package Java_Point_Practice;

public class Type_of_variables {
	
		static int m = 10;
		void method()
		//static variables.
		{
			int n =20;
			//Local Variables.
			System.out.println(n);
		
		}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int p = 30;
		int o = m+p;
		System.out.println(o);

	}

}
